// Process and structure GitHub release data
const processedReleases = [];

for (const item of $input.all()) {
  if (item.json && Array.isArray(item.json)) {
    for (const release of item.json) {
      const isVue = release.html_url.includes('/vuejs/');
      const isNuxt = release.html_url.includes('/nuxt/');

      const processedRelease = {
        framework: isVue ? 'Vue.js' : isNuxt ? 'Nuxt.js' : 'Unknown',
        version: release.tag_name,
        name: release.name || release.tag_name,
        description: release.body ? release.body.substring(0, 500) + '...' : 'No description available',
        published_date: release.published_at,
        html_url: release.html_url,
        draft: release.draft,
        prerelease: release.prerelease,
        author: release.author ? release.author.login : 'Unknown',
        assets_count: release.assets ? release.assets.length : 0,
        download_count: release.assets ? 
          release.assets.reduce((sum, asset) => sum + (asset.download_count || 0), 0) : 0,
        created_at: release.created_at,
        repository: isVue ? 'vuejs/core' : isNuxt ? 'nuxt/nuxt' : 'unknown',
        // Add formatted fields for display
        published_date_formatted: new Date(release.published_at).toLocaleDateString('en-US', {
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        }),
        is_major: release.tag_name.match(/\.(0)\.0/) ? true : false,
        is_minor: release.tag_name.match(/\d+\.(\d+)\.0/) && !release.tag_name.match(/\.(0)\.0/) ? true : false,
        version_type: release.prerelease ? 'Prerelease' : release.draft ? 'Draft' : 'Stable'
      };

      processedReleases.push(processedRelease);
    }
  }
}

// Sort by published date (newest first)
processedReleases.sort((a, b) => new Date(b.published_date) - new Date(a.published_date));

return processedReleases.map(release => ({ json: release }));

// Create formatted outputs for Slack and Obsidian (NO console.log)
const releases = $input.all();
const vueReleases = releases.filter(r => r.json.framework === 'Vue.js');
const nuxtReleases = releases.filter(r => r.json.framework === 'Nuxt.js');

const currentWeek = new Date();
const oneWeekAgo = new Date(currentWeek.getTime() - (7 * 24 * 60 * 60 * 1000));

// Filter recent releases (last week)
const recentReleases = releases.filter(r => 
  new Date(r.json.published_date) > oneWeekAgo
);

// Create Slack message format
const slackMessage = {
  text: '🚀 Weekly Vue.js & Nuxt.js Releases Summary',
  blocks: [
    {
      type: 'header',
      text: {
        type: 'plain_text',
        text: '🚀 Vue.js & Nuxt.js Weekly Release Summary'
      }
    },
    {
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: `📊 *Summary for ${currentWeek.toLocaleDateString('en-US')}*\n• Total releases checked: ${releases.length}\n• Vue.js releases: ${vueReleases.length}\n• Nuxt.js releases: ${nuxtReleases.length}\n• Recent releases (last 7 days): ${recentReleases.length}`
      }
    }
  ]
};

// Add recent releases to Slack message
if (recentReleases.length > 0) {
  slackMessage.blocks.push({
    type: 'section',
    text: {
      type: 'mrkdwn',
      text: '*🆕 Recent Releases:*'
    }
  });

  recentReleases.slice(0, 5).forEach(r => {
    const release = r.json;
    const emoji = release.framework === 'Vue.js' ? '💚' : '🟢';
    slackMessage.blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: `${emoji} *${release.framework} ${release.version}*\n📅 ${release.published_date_formatted}\n🔗 <${release.html_url}|View Release>`
      }
    });
  });
} else {
  slackMessage.blocks.push({
    type: 'section',
    text: {
      type: 'mrkdwn',
      text: '🔍 No new releases in the last week.'
    }
  });
}

// Add latest versions section
if (vueReleases.length > 0 || nuxtReleases.length > 0) {
  let latestText = '*📋 Current Latest Versions:*\n';
  if (vueReleases.length > 0) {
    const latest = vueReleases[0].json;
    latestText += `💚 Vue.js: ${latest.version} (${latest.published_date_formatted})\n`;
  }
  if (nuxtReleases.length > 0) {
    const latest = nuxtReleases[0].json;
    latestText += `🟢 Nuxt.js: ${latest.version} (${latest.published_date_formatted})\n`;
  }

  slackMessage.blocks.push({
    type: 'section',
    text: {
      type: 'mrkdwn',
      text: latestText
    }
  });
}

// Create Obsidian note format
const obsidianNote = {
  title: `Vue & Nuxt Releases - ${currentWeek.toLocaleDateString('en-US')}`,
  content: `# Vue.js & Nuxt.js Release Summary\n\n**Date:** ${currentWeek.toLocaleDateString('en-US')}\n**Collection Time:** ${new Date().toISOString()}\n\n## 📊 Summary\n\n- **Total releases checked:** ${releases.length}\n- **Vue.js releases:** ${vueReleases.length}\n- **Nuxt.js releases:** ${nuxtReleases.length}\n- **Recent releases (last 7 days):** ${recentReleases.length}\n\n## 🆕 Recent Releases\n\n${recentReleases.length > 0 ? recentReleases.map(r => {
    const release = r.json;
    return `### ${release.framework} ${release.version}\n\n- **Published:** ${release.published_date_formatted}\n- **Type:** ${release.version_type}\n- **Repository:** ${release.repository}\n- **URL:** [${release.html_url}](${release.html_url})\n- **Description:** ${release.description}\n`;
  }).join('\n') : 'No new releases in the last week.'}\n\n## 📋 Latest Versions\n\n${vueReleases.length > 0 ? `**Vue.js:** ${vueReleases[0].json.version} (${vueReleases[0].json.published_date_formatted})\n` : ''}${nuxtReleases.length > 0 ? `**Nuxt.js:** ${nuxtReleases[0].json.version} (${nuxtReleases[0].json.published_date_formatted})\n` : ''}\n## 🏷️ Tags\n\n#vue #nuxt #releases #weekly-summary\n\n---\n*Generated automatically by Vue-Nuxt News Collector*`
};

// Create summary with all formats (NO console.log statements)
const summary = {
  // Raw data
  total_releases: releases.length,
  vue_releases: vueReleases.length,
  nuxt_releases: nuxtReleases.length,
  recent_releases: recentReleases.length,
  latest_vue: vueReleases.length > 0 ? vueReleases[0].json : null,
  latest_nuxt: nuxtReleases.length > 0 ? nuxtReleases[0].json : null,
  collection_timestamp: new Date().toISOString(),

  // Formatted outputs
  slack_message: slackMessage,
  obsidian_note: obsidianNote,

  // All releases data
  releases: releases.map(r => r.json),
  recent_releases_data: recentReleases.map(r => r.json),

  // Success indicator
  success: true,
  message: "Vue & Nuxt releases collected and formatted successfully!"
};

return [{ json: summary }];
