// Simple processing: handle both array and single releases
const processedReleases = [];

// Get all inputs
const inputs = $input.all();

for (const item of inputs) {
  // Handle both single release objects and arrays of releases
  const releases = Array.isArray(item.json) ? item.json : [item.json];

  for (const release of releases) {
    if (!release || !release.html_url) continue;

    const isVue = release.html_url.includes('/vuejs/');
    const isNuxt = release.html_url.includes('/nuxt/');

    processedReleases.push({
      framework: isVue ? 'Vue.js' : isNuxt ? 'Nuxt.js' : 'Unknown',
      version: release.tag_name,
      name: release.name || release.tag_name,
      description: release.body ? release.body.substring(0, 500) + '...' : 'No description available',
      published_date: release.published_at,
      html_url: release.html_url,
      draft: release.draft,
      prerelease: release.prerelease,
      author: release.author ? release.author.login : 'Unknown',
      repository: isVue ? 'vuejs/core' : isNuxt ? 'nuxt/nuxt' : 'unknown',
      published_date_formatted: new Date(release.published_at).toLocaleDateString('en-US', {
        year: 'numeric', month: 'long', day: 'numeric'
      }),
      version_type: release.prerelease ? 'Prerelease' : release.draft ? 'Draft' : 'Stable'
    });
  }
}

// Sort by date (newest first)
processedReleases.sort((a, b) => new Date(b.published_date) - new Date(a.published_date));

return processedReleases.map(release => ({ json: release }));

// Create formatted outputs for Slack and Obsidian
const releases = $input.all();
const vueReleases = releases.filter(r => r.json.framework === 'Vue.js');
const nuxtReleases = releases.filter(r => r.json.framework === 'Nuxt.js');

const currentWeek = new Date();
const oneWeekAgo = new Date(currentWeek.getTime() - (7 * 24 * 60 * 60 * 1000));

// Filter recent releases
const recentReleases = releases.filter(r => new Date(r.json.published_date) > oneWeekAgo);

// Create Slack message
const slackMessage = {
  text: 'ðŸš€ Weekly Vue.js & Nuxt.js Releases Summary',
  blocks: [
    {
      type: 'header',
      text: {
        type: 'plain_text',
        text: 'ðŸš€ Vue.js & Nuxt.js Weekly Release Summary'
      }
    },
    {
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: `ðŸ“Š *Summary for ${currentWeek.toLocaleDateString('en-US')}*\nâ€¢ Total releases checked: ${releases.length}\nâ€¢ Vue.js releases: ${vueReleases.length}\nâ€¢ Nuxt.js releases: ${nuxtReleases.length}\nâ€¢ Recent releases (last 7 days): ${recentReleases.length}`
      }
    }
  ]
};

// Add recent releases
if (recentReleases.length > 0) {
  slackMessage.blocks.push({
    type: 'section',
    text: { type: 'mrkdwn', text: '*ðŸ†• Recent Releases:*' }
  });

  recentReleases.slice(0, 5).forEach(r => {
    const release = r.json;
    const emoji = release.framework === 'Vue.js' ? 'ðŸ’š' : 'ðŸŸ¢';
    slackMessage.blocks.push({
      type: 'section',
      text: {
        type: 'mrkdwn',
        text: `${emoji} *${release.framework} ${release.version}*\nðŸ“… ${release.published_date_formatted}\nðŸ”— <${release.html_url}|View Release>`
      }
    });
  });
} else {
  slackMessage.blocks.push({
    type: 'section',
    text: { type: 'mrkdwn', text: 'ðŸ” No new releases in the last week.' }
  });
}

// Add latest versions
if (vueReleases.length > 0 || nuxtReleases.length > 0) {
  let latestText = '*ðŸ“‹ Current Latest Versions:*\n';
  if (vueReleases.length > 0) {
    const latest = vueReleases[0].json;
    latestText += `ðŸ’š Vue.js: ${latest.version} (${latest.published_date_formatted})\n`;
  }
  if (nuxtReleases.length > 0) {
    const latest = nuxtReleases[0].json;
    latestText += `ðŸŸ¢ Nuxt.js: ${latest.version} (${latest.published_date_formatted})\n`;
  }
  slackMessage.blocks.push({ type: 'section', text: { type: 'mrkdwn', text: latestText } });
}

// Create summary
const summary = {
  success: true,
  message: "Vue & Nuxt releases collected and formatted successfully!",
  total_releases: releases.length,
  vue_releases: vueReleases.length,
  nuxt_releases: nuxtReleases.length,
  recent_releases: recentReleases.length,
  latest_vue: vueReleases.length > 0 ? vueReleases[0].json : null,
  latest_nuxt: nuxtReleases.length > 0 ? nuxtReleases[0].json : null,
  collection_timestamp: new Date().toISOString(),
  slack_message: slackMessage,
  releases: releases.map(r => r.json)
};

return [{ json: summary }];
