// Parse RSS feed and extract latest article information
const input = $input.first();

let rssXml = '';

// Extract RSS XML from various possible input formats
if (typeof input === 'string') {
  rssXml = input;
} else if (input?.data && typeof input.data === 'string') {
  rssXml = input.data;
} else if (input?.json && typeof input.json === 'string') {
  rssXml = input.json;
} else {
  console.error('No valid RSS data found in input');
  return [{ json: { error: 'No RSS data found', input_type: typeof input } }];
}

console.log(`RSS XML received: ${rssXml.length} characters`);

// Extract RSS items using regex
const itemMatches = rssXml.match(/<item[^>]*>([\s\S]*?)<\/item>/g);

if (!itemMatches?.length) {
  console.error('No RSS items found in XML');
  return [{ json: { error: 'No RSS items found in feed' } }];
}

console.log(`Found ${itemMatches.length} RSS items`);

// Parse the latest item (first in list)
const latestItem = itemMatches[0];

// Extract data using robust regex patterns
const titleMatch = latestItem.match(/<title><!\[CDATA\[([^\]]+)\]\]><\/title>/) || 
                  latestItem.match(/<title>([^<]+)<\/title>/);
const linkMatch = latestItem.match(/<link>([^<]+)<\/link>/);
const pubDateMatch = latestItem.match(/<pubDate>([^<]+)<\/pubDate>/);
const descriptionMatch = latestItem.match(/<description><!\[CDATA\[([^\]]+)\]\]><\/description>/) || 
                        latestItem.match(/<description>([^<]+)<\/description>/);

const title = titleMatch?.[1] || 'No title available';
const link = linkMatch?.[1] || '';
const pubDate = pubDateMatch?.[1] || '';
const description = descriptionMatch?.[1] || '';

// Parse and format publication date
const publishedDate = pubDate ? new Date(pubDate) : new Date();
const daysAgo = Math.floor((Date.now() - publishedDate.getTime()) / (1000 * 60 * 60 * 24));
const formattedDate = publishedDate.toLocaleDateString('en-US', {
  year: 'numeric', 
  month: 'long', 
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

// Extract issue number
const issueMatch = title.match(/#(\d+)/);
const issueNumber = issueMatch?.[1] || 'Unknown';

// Create a smart summary based on title analysis
let smartSummary = `Weekly Vue News #${issueNumber}: `;
const titleLower = title.toLowerCase();

if (titleLower.includes('vue') && titleLower.includes('nuxt')) {
  smartSummary += 'Covers both Vue.js and Nuxt.js ecosystem updates. ';
} else if (titleLower.includes('nuxt')) {
  smartSummary += 'Focuses on Nuxt.js framework developments. ';
} else if (titleLower.includes('vue')) {
  smartSummary += 'Highlights Vue.js framework updates. ';
}

// Extract key topics from title
const topics = [];
if (titleLower.includes('ui')) topics.push('UI components');
if (titleLower.match(/v\d+/)) topics.push('version updates');
if (titleLower.includes('real-time')) topics.push('real-time features');
if (titleLower.includes('component')) topics.push('component development');
if (titleLower.includes('communication')) topics.push('communication patterns');
if (titleLower.includes('performance')) topics.push('performance optimization');
if (titleLower.includes('typescript')) topics.push('TypeScript integration');

if (topics.length > 0) {
  smartSummary += `Key topics include: ${topics.join(', ')}.`;
} else {
  smartSummary += 'Covers latest framework updates, tools, and community resources.';
}

// Return structured data
const result = {
  type: 'weekly_vue_news',
  title,
  issue_number: issueNumber,
  link,
  publication_date: pubDate,
  publication_date_formatted: formattedDate,
  days_since_published: daysAgo,
  description,
  source: 'Weekly Vue News',
  author: 'Michael Hoffmann',
  smart_summary: smartSummary,
  has_ai_summary: false,
  is_latest: true
};

console.log(`Parsed RSS item: ${title}`);
return [{ json: result }];

// Process all GitHub data (releases, repo info, contributors)
const vueRelease = $('Vue.js Release').first()?.json;
const vueRepoInfo = $('Vue.js Repo Info').first()?.json;
const vueContributors = $('Vue.js Contributors').all().map(item => item.json).filter(Boolean);
const nuxtRelease = $('Nuxt.js Release').first()?.json;
const nuxtRepoInfo = $('Nuxt.js Repo Info').first()?.json;
const nuxtContributors = $('Nuxt.js Contributors').all().map(item => item.json).filter(Boolean);

console.log('=== PROCESSING ENHANCED GITHUB DATA ===');
console.log('Vue release:', vueRelease ? 'Available' : 'Missing');
console.log('Vue repo info:', vueRepoInfo ? 'Available' : 'Missing');
console.log('Vue contributors:', vueContributors.length, 'items');
console.log('Nuxt release:', nuxtRelease ? 'Available' : 'Missing');
console.log('Nuxt repo info:', nuxtRepoInfo ? 'Available' : 'Missing');
console.log('Nuxt contributors:', nuxtContributors.length, 'items');

// Helper function for safe data extraction
function safeExtract(data, path, fallback = 'Unknown') {
  try {
    return path.split('.').reduce((obj, key) => obj?.[key], data) ?? fallback;
  } catch {
    return fallback;
  }
}

// Format date helper
function formatReleaseDate(dateStr) {
  if (!dateStr) return 'Unknown';
  const date = new Date(dateStr);
  const daysAgo = Math.floor((Date.now() - date.getTime()) / (1000 * 60 * 60 * 24));
  const formatted = date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });
  return `${formatted} (${daysAgo} days ago)`;
}

// Format numbers with thousands separator
function formatNumber(num) {
  return num ? num.toLocaleString() : '0';
}

const frameworks = [];

// Process Vue.js data
if (vueRelease && !vueRelease.error && vueRelease.tag_name) {
  const topContributors = vueContributors.slice(0, 3).map(contributor => 
    `${safeExtract(contributor, 'login')} (${formatNumber(safeExtract(contributor, 'contributions', 0))})`
  ).join(', ');

  const vueFramework = {
    name: 'Vue.js',
    emoji: 'üíö',
    // Release data
    version: safeExtract(vueRelease, 'tag_name'),
    release_name: safeExtract(vueRelease, 'name'),
    release_date_formatted: formatReleaseDate(vueRelease.published_at),
    is_prerelease: vueRelease.prerelease || false,
    release_author: safeExtract(vueRelease, 'author.login'),
    release_url: safeExtract(vueRelease, 'html_url', ''),
    changelog_preview: safeExtract(vueRelease, 'body', '').substring(0, 200).replace(/\n/g, ' ').trim() + (vueRelease.body && vueRelease.body.length > 200 ? '...' : ''),
    // Repository data
    stars: formatNumber(safeExtract(vueRepoInfo, 'stargazers_count', 0)),
    forks: formatNumber(safeExtract(vueRepoInfo, 'forks_count', 0)),
    open_issues: formatNumber(safeExtract(vueRepoInfo, 'open_issues_count', 0)),
    language: safeExtract(vueRepoInfo, 'language', 'Unknown'),
    repo_updated: formatReleaseDate(safeExtract(vueRepoInfo, 'pushed_at')),
    // Contributors
    top_contributors: topContributors || 'No contributors data',
    total_contributors: vueContributors.length
  };
  frameworks.push(vueFramework);
  console.log(`Processed Vue.js: ${vueFramework.version}`);
}

// Process Nuxt.js data
if (nuxtRelease && !nuxtRelease.error && nuxtRelease.tag_name) {
  const topContributors = nuxtContributors.slice(0, 3).map(contributor => 
    `${safeExtract(contributor, 'login')} (${formatNumber(safeExtract(contributor, 'contributions', 0))})`
  ).join(', ');

  const nuxtFramework = {
    name: 'Nuxt.js',
    emoji: 'üü¢',
    // Release data
    version: safeExtract(nuxtRelease, 'tag_name'),
    release_name: safeExtract(nuxtRelease, 'name'),
    release_date_formatted: formatReleaseDate(nuxtRelease.published_at),
    is_prerelease: nuxtRelease.prerelease || false,
    release_author: safeExtract(nuxtRelease, 'author.login'),
    release_url: safeExtract(nuxtRelease, 'html_url', ''),
    changelog_preview: safeExtract(nuxtRelease, 'body', '').substring(0, 200).replace(/\n/g, ' ').trim() + (nuxtRelease.body && nuxtRelease.body.length > 200 ? '...' : ''),
    // Repository data
    stars: formatNumber(safeExtract(nuxtRepoInfo, 'stargazers_count', 0)),
    forks: formatNumber(safeExtract(nuxtRepoInfo, 'forks_count', 0)),
    open_issues: formatNumber(safeExtract(nuxtRepoInfo, 'open_issues_count', 0)),
    language: safeExtract(nuxtRepoInfo, 'language', 'Unknown'),
    repo_updated: formatReleaseDate(safeExtract(nuxtRepoInfo, 'pushed_at')),
    // Contributors
    top_contributors: topContributors || 'No contributors data',
    total_contributors: nuxtContributors.length
  };
  frameworks.push(nuxtFramework);
  console.log(`Processed Nuxt.js: ${nuxtFramework.version}`);
}

if (frameworks.length === 0) {
  return [{ json: { error: 'No valid GitHub framework data available' } }];
}

console.log(`Successfully processed ${frameworks.length} frameworks with enhanced data`);
return [{ json: { frameworks, total_count: frameworks.length } }];

