// Create Enhanced GitHub Releases Slack Message
const githubData = $input.first()?.json;
const timestamp = new Date().toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

if (!githubData || githubData.error || !githubData.frameworks) {
  return [{
    json: {
      success: false,
      slack_message: `‚ùå *GitHub Releases - Error*\n\nüïê ${timestamp}\n\n*Error:* ${githubData?.error || 'No GitHub data available'}\n\nüîß Please check GitHub API configuration.`,
