// SEQUENTIAL APPROACH: Process all data in sequence
const currentInput = $input.first().json;

console.log('=== SEQUENTIAL DATA PROCESSING ===');

// Access data from previous node executions
const vueRelease = $('1. Vue Release').first()?.json;
const vueRepo = $('2. Vue Repo Info').first()?.json;
const vueContributors = $('3. Vue Contributors').first()?.json;
const nuxtRelease = $('4. Nuxt Release').first()?.json;
const nuxtRepo = $('5. Nuxt Repo Info').first()?.json;
const nuxtContributors = $('6. Nuxt Contributors').first()?.json;

// Helper functions
function extractRelease(data) {
  if (Array.isArray(data) && data.length > 0 && data[0].tag_name) {
    return data[0];
  } else if (data && data.tag_name) {
    return data;
  }
  return null;
}

function safeGet(obj, path, fallback) {
  try {
    return path.split('.').reduce((o, p) => o && o[p], obj) || fallback;
  } catch {
    return fallback;
  }
}

// Process Vue.js data
const vueReleaseData = extractRelease(vueRelease);
const vueData = vueReleaseData ? {
  framework: 'Vue.js',
  repository: 'vuejs/core',
  latest_version: safeGet(vueReleaseData, 'tag_name', 'Unknown'),
  release_name: safeGet(vueReleaseData, 'name', 'No name'),
  release_date_formatted: vueReleaseData.published_at ? new Date(vueReleaseData.published_at).toLocaleDateString('en-US', {
    year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit'
  }) : 'Unknown',
  days_since_release: vueReleaseData.published_at ? Math.floor((new Date() - new Date(vueReleaseData.published_at)) / (1000 * 60 * 60 * 24)) : 0,
  is_prerelease: safeGet(vueReleaseData, 'prerelease', false),
  release_author: safeGet(vueReleaseData, 'author.login', 'Unknown'),
  changelog: safeGet(vueReleaseData, 'body', 'No changelog available'),
  changelog_length: (safeGet(vueReleaseData, 'body', '') || '').length,
  repo_stars: safeGet(vueRepo, 'stargazers_count', 0),
  repo_forks: safeGet(vueRepo, 'forks_count', 0),
  repo_watchers: safeGet(vueRepo, 'watchers_count', 0),
  repo_open_issues: safeGet(vueRepo, 'open_issues_count', 0),
  repo_language: safeGet(vueRepo, 'language', 'Unknown'),
  repo_license: safeGet(vueRepo, 'license.name', 'Unknown'),
  release_url: safeGet(vueReleaseData, 'html_url', ''),
  top_contributors: (vueContributors && Array.isArray(vueContributors) ? vueContributors : []).slice(0, 5).map(c => ({
    login: safeGet(c, 'login', 'Unknown'),
    contributions: safeGet(c, 'contributions', 0),
    profile_url: safeGet(c, 'html_url', '')
  })),
  total_contributors_shown: (vueContributors && Array.isArray(vueContributors) ? vueContributors : []).length
} : null;

// Process Nuxt.js data
const nuxtReleaseData = extractRelease(nuxtRelease);
const nuxtData = nuxtReleaseData ? {
  framework: 'Nuxt.js',
  repository: 'nuxt/nuxt',
  latest_version: safeGet(nuxtReleaseData, 'tag_name', 'Unknown'),
  release_name: safeGet(nuxtReleaseData, 'name', 'No name'),
  release_date_formatted: nuxtReleaseData.published_at ? new Date(nuxtReleaseData.published_at).toLocaleDateString('en-US', {
    year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit'
  }) : 'Unknown',
  days_since_release: nuxtReleaseData.published_at ? Math.floor((new Date() - new Date(nuxtReleaseData.published_at)) / (1000 * 60 * 60 * 24)) : 0,
  is_prerelease: safeGet(nuxtReleaseData, 'prerelease', false),
  release_author: safeGet(nuxtReleaseData, 'author.login', 'Unknown'),
  changelog: safeGet(nuxtReleaseData, 'body', 'No changelog available'),
  changelog_length: (safeGet(nuxtReleaseData, 'body', '') || '').length,
  repo_stars: safeGet(nuxtRepo, 'stargazers_count', 0),
  repo_forks: safeGet(nuxtRepo, 'forks_count', 0),
  repo_watchers: safeGet(nuxtRepo, 'watchers_count', 0),
  repo_open_issues: safeGet(nuxtRepo, 'open_issues_count', 0),
  repo_language: safeGet(nuxtRepo, 'language', 'Unknown'),
  repo_license: safeGet(nuxtRepo, 'license.name', 'Unknown'),
  release_url: safeGet(nuxtReleaseData, 'html_url', ''),
  top_contributors: (nuxtContributors && Array.isArray(nuxtContributors) ? nuxtContributors : []).slice(0, 5).map(c => ({
    login: safeGet(c, 'login', 'Unknown'),
    contributions: safeGet(c, 'contributions', 0),
    profile_url: safeGet(c, 'html_url', '')
  })),
  total_contributors_shown: (nuxtContributors && Array.isArray(nuxtContributors) ? nuxtContributors : []).length
} : null;

if (!vueData && !nuxtData) {
  return [{ json: { error: 'No valid data found for Vue.js or Nuxt.js' } }];
}

return [
  ...(vueData ? [{ json: vueData }] : []),
  ...(nuxtData ? [{ json: nuxtData }] : [])
];

